import socketfrom lgoledctrl import WebOSRemotefrom telnetlib import Telnetimport osimport sysSCREEN_MUTE_OFF = "0000000000000002 00 KEY_B lircd.conf"SCREEN_MUTE_ON = "0000000000000001 00 KEY_A lircd.conf"PIONEER_BLUE = "00000000a55a7a85 00 KEY_FN_1 lvrm2"PIONEER_GREEN = "00000000a55a02fd 00 KEY_FN_2 lvrm2"PIONEER_RED = "00000000a55a6a95 00 KEY_FN_B lvrm2"PIONEER_YELLOW = "00000000a55a6a95 00 KEY_FN_D lvrm2"PIONEER_KEY_LEFT = "00000000a55ac13e 00 KEY_LEFT lvrm2"PIONEER_KEY_RIGHT = "00000000a55a41be 00 KEY_RIGHT lvrm2"PIONEER_KEY_UP = "00000000a55a01fe 00 KEY_UP lvrm2"PIONEER_KEY_DOWN = "00000000a55a817e 00 KEY_DOWN lvrm2"client = socket.socket(socket.AF_UNIX, socket.SOCK_STREAM)socket_address = "/var/run/lirc/lircd"try:    client.connect(socket_address)except OSError as e:    print("Could not connect to "+socket_address+str(e))tv = WebOSRemote("10.0.0.33")while True:    print("Before Transmission")    btnString = client.recv(4096).decode("utf-8").strip()    print("Begin Transmission: \n" + btnString + "\nEnd of Transmission")    print("After Transmission")    if btnString == PIONEER_BLUE:        tv.power_on()        print("Power On")    if btnString == PIONEER_GREEN:        #tv.powerOff() #Cant power back on yet        print("Power Off")    if btnString == SCREEN_MUTE_ON:        tv.screen_mute_on()        print("Screen mute")    if btnString == SCREEN_MUTE_OFF:        tv.screen_mute_off()        print("Screen mute off")    if btnString == PIONEER_KEY_DOWN:        tv.next_format()    else:        print(btnString)        print("Command not recognized")client.close()